/* Falling Red Dots Animation */
.falling-dots {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: -1;
  overflow: hidden;
}

.falling-dots::before {
  content: '';
  position: absolute;
  width: 100%;
  height: 100%;
  background-image: 
    radial-gradient(2px 2px at 20px 30px, hsl(248, 100%, 34%), transparent),
    radial-gradient(2px 2px at 40px 70px, hsl(248, 100%, 34%), transparent),
    radial-gradient(1px 1px at 90px 40px, hsl(248, 100%, 34%), transparent),
    radial-gradient(1px 1px at 130px 80px, hsl(248, 100%, 34%), transparent),
    radial-gradient(2px 2px at 160px 30px, hsl(248, 100%, 34%), transparent);
  background-repeat: repeat;
  background-size: 200px 100px;
  animation: falling-dots-animation 15s linear infinite;
}

.falling-dots::after {
  content: '';
  position: absolute;
  width: 100%;
  height: 100%;
  background-image: 
    radial-gradient(1px 1px at 60px 10px, hsl(248, 100%, 20%), transparent),
    radial-gradient(2px 2px at 100px 50px, hsl(248, 100%, 20%), transparent),
    radial-gradient(1px 1px at 140px 20px, hsl(248, 100%, 20%), transparent),
    radial-gradient(1px 1px at 180px 60px, hsl(248, 100%, 20%), transparent);
  background-repeat: repeat;
  background-size: 250px 120px;
  animation: falling-dots-animation 20s linear infinite;
  animation-delay: -10s;
}

@keyframes falling-dots-animation {
  0% {
    transform: translateY(-100vh) rotateZ(0deg);
    opacity: 0;
  }
  10% {
    opacity: 0.6;
  }
  90% {
    opacity: 0.6;
  }
  100% {
    transform: translateY(100vh) rotateZ(360deg);
    opacity: 0;
  }
}

