          {/* Logo and Branding */}
          <div className="flex items-center space-x-4">
            <div className="text-2xl font-bold text-primary flex items-center" data-testid="brand-logo">
              <img src={logoImage} alt="PlayDirty" className="w-8 h-8 mr-2" />
              PlayDirty
            </div>
          </div>

          {/* Search Bar - Desktop */}
          <div className="hidden md:flex flex-1 max-w-md mx-8">
            <div className="relative w-full">
              <Input
                type="text"
                placeholder="Search products..."
                value={searchQuery}
                onChange={(e) => onSearchChange(e.target.value)}
                className="w-full bg-input text-foreground px-4 py-2 pl-10 rounded-lg border border-border focus:border-primary focus:outline-none search-glow transition-all duration-300"
                data-testid="input-search-desktop"
              />
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
            </div>
          </div>

          {/* Navigation Links - Desktop */}
          <nav className="hidden lg:flex items-center space-x-6">
            <a href="/" className="text-foreground hover:text-primary transition-colors" data-testid="link-home">
              Home
            </a>
            <a href="/products" className="text-foreground hover:text-primary transition-colors" data-testid="link-products">
              Products
            </a>
            <a href="/support" className="text-foreground hover:text-primary transition-colors" data-testid="link-support">
              Support
            </a>
            <a href="/about" className="text-foreground hover:text-primary transition-colors" data-testid="link-about">
              About
            </a>

            {/* Authentication Links */}
            {user ? (
              <div className="flex items-center space-x-4">
                <a href="/dashboard" className="text-foreground hover:text-primary transition-colors border border-primary/20 px-3 py-1 rounded flex items-center" data-testid="link-dashboard">
                  <User className="w-4 h-4 mr-1" />
                  {user.username}
                </a>
                <a href="/profile" className="text-foreground hover:text-primary transition-colors px-3 py-1 rounded flex items-center" data-testid="link-profile">
                  Profile
                </a>
                <Button
                  onClick={handleLogout}
                  variant="ghost"
                  size="sm"
                  className="text-foreground hover:text-primary"
                  disabled={logoutMutation.isPending}
                  data-testid="button-logout"
                >
                  <LogOut className="w-4 h-4" />
                </Button>
              </div>
            ) : (
              <a href="/auth" className="text-foreground hover:text-primary transition-colors border border-primary/20 px-3 py-1 rounded" data-testid="link-auth">
                Sign In
              </a>
            )}
          </nav>

          {/* Mobile Menu Button */}
          <Sheet open={isMobileMenuOpen} onOpenChange={setIsMobileMenuOpen}>
            <SheetTrigger asChild>
              <Button variant="ghost" size="icon" className="md:hidden" data-testid="button-mobile-menu">
                <Menu className="h-6 w-6" />
              </Button>
            </SheetTrigger>
            <SheetContent side="right" className="w-64">
              <nav className="flex flex-col space-y-4 mt-8">
                <a href="/" className="text-foreground hover:text-primary transition-colors" data-testid="link-mobile-home" onClick={() => setIsMobileMenuOpen(false)}>
                  Home
                </a>
                <a href="/products" className="text-foreground hover:text-primary transition-colors" data-testid="link-mobile-products" onClick={() => setIsMobileMenuOpen(false)}>
                  Products
                </a>
                <a href="/support" className="text-foreground hover:text-primary transition-colors" data-testid="link-mobile-support" onClick={() => setIsMobileMenuOpen(false)}>
                  Support
                </a>
                <a href="/about" className="text-foreground hover:text-primary transition-colors" data-testid="link-mobile-about" onClick={() => setIsMobileMenuOpen(false)}>
                  About
                </a>

                {/* Mobile Authentication Links */}
                {user ? (
                  <>
                    <a href="/dashboard" className="text-foreground hover:text-primary transition-colors border border-primary/20 px-3 py-1 rounded flex items-center" data-testid="link-mobile-dashboard" onClick={() => setIsMobileMenuOpen(false)}>
                      <User className="w-4 h-4 mr-2" />
                      {user.username}
                    </a>
                    <a href="/profile" className="text-foreground hover:text-primary transition-colors px-3 py-1 rounded flex items-center" data-testid="link-mobile-profile" onClick={() => setIsMobileMenuOpen(false)}>
                      Profile
                    </a>
                    <Button
                      onClick={handleLogout}
                      variant="ghost"
                      className="text-foreground hover:text-primary justify-start px-0"
                      disabled={logoutMutation.isPending}
                      data-testid="button-mobile-logout"
                    >
                      <LogOut className="w-4 h-4 mr-2" />
                      {logoutMutation.isPending ? "Logging out..." : "Logout"}
                    </Button>
                  </>
                ) : (
                  <a href="/auth" className="text-foreground hover:text-primary transition-colors border border-primary/20 px-3 py-1 rounded" data-testid="link-mobile-auth" onClick={() => setIsMobileMenuOpen(false)}>
                    Sign In
                  </a>
                )}
              </nav>
            </SheetContent>
          </Sheet>
        </div>

        {/* Mobile Search */}
        <div className="md:hidden mt-4">
          <div className="relative">
            <Input
              type="text"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => onSearchChange(e.target.value)}
              className="w-full bg-input text-foreground px-4 py-2 pl-10 rounded-lg border border-border focus:border-primary focus:outline-none"
              data-testid="input-search-mobile"
            />
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
          </div>
        </div>
      </div>
    </header>
  );
}

client/src/components/product-card.tsx.content:
import { Product } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Check, X, Package, Eye, Star, ShoppingCart } from "lucide-react";
import { useLocation } from "wouter";
import { useAuth } from "@/hooks/use-auth";

interface ProductCardProps {
  product: Product;
  className?: string;
  compact?: boolean;
}

export default function ProductCard({ product, className = "", compact = false }: ProductCardProps) {
  const [, setLocation] = useLocation();
  const isInStock = product.inStock && product.stockQuantity > 0;
  const hasDiscount = product.originalPrice && parseFloat(product.originalPrice) > parseFloat(product.price);

  const handleCheckoutClick = () => {
    const productData = encodeURIComponent(JSON.stringify(product));
    setLocation(`/checkout/${productData}`);
  };

  const { user } = useAuth();

  const handleViewDetails = async () => {
    // Track product view for authenticated users
    if (user) {
      try {
        await fetch(`/api/recently-viewed/${product.id}`, {
          method: 'POST',
          credentials: 'include'
        });
      } catch (error) {
        // Silently ignore tracking errors to not disrupt user experience
        console.debug('Failed to track product view:', error);
      }
    }

    setLocation(`/product/${product.id}`);
  };

  return (
    <div className={`bg-card rounded-lg border border-border overflow-hidden card-hover neon-border ${!isInStock ? 'opacity-75' : ''} ${className}`} data-testid={`card-product-${product.id}`}>
      <div className={`relative bg-muted/20 ${compact ? 'p-3' : 'p-6'} border-b border-border`}>
        {!compact && (
          <div className="flex justify-between items-start mb-4">
            <span className="px-2 py-1 bg-primary text-primary-foreground text-xs font-medium rounded-md">
              {product.category}
            </span>
            {hasDiscount && (
              <span className="px-2 py-1 bg-purple-600 text-white text-xs font-medium rounded-md">
                SALE
              </span>
            )}
          </div>
        )}
        <div className="text-center cursor-pointer" onClick={handleViewDetails}>
          {product.imageUrl ? (
            <img 
              src={product.imageUrl} 
              alt={product.name}
              className={`${compact ? 'w-full h-24' : 'w-[360px] h-auto'} mx-auto mb-2 rounded-md object-cover hover:opacity-90 transition-opacity`}
            />
          ) : (
            <Package className={`${compact ? 'w-6 h-6' : 'w-12 h-12'} text-muted-foreground mx-auto mb-2`} />
          )}
          <h3 className={`${compact ? 'text-sm' : 'text-lg'} font-semibold hover:text-primary transition-colors ${compact ? 'truncate' : ''}`} data-testid={`text-product-name-${product.id}`}>
            {product.name}
          </h3>
        </div>
      </div>

      <div className={`${compact ? 'p-3' : 'p-6'}`}>
        <div className="flex items-center justify-between mb-2">
          <div className="flex items-center gap-2">
            <Package className="w-4 h-4 text-muted-foreground" />
            <span className="text-sm text-muted-foreground">
              {product.game}
            </span>
          </div>
          <span 
            className={`text-xs px-2 py-1 rounded-full flex items-center space-x-1 ${
              isInStock 
                ? 'bg-green-600 text-white' 
                : 'bg-purple-600 text-white'
            }`}
            data-testid={`status-stock-${product.id}`}
          >
            {isInStock ? (
              <>
                <Check className="w-3 h-3" />
                <span>
                  {product.stockQuantity > 1 ? `${product.stockQuantity} In Stock` : 'In Stock'}
                </span>
              </>
            ) : (
              <>
                <X className="w-3 h-3" />
                <span>Out of Stock</span>
              </>
            )}
          </span>
        </div>

        <p className="text-muted-foreground text-sm mb-3" data-testid={`text-product-description-${product.id}`}>
          {product.description}
        </p>

        {/* Rating Display */}
        {((product.averageRating ?? 0) > 0 && (product.reviewCount ?? 0) > 0) && (
          <div className="flex items-center space-x-2 mb-3">
            <div className="flex items-center">
              {[1, 2, 3, 4, 5].map((star) => (
                <Star
                  key={star}
                  className={`w-4 h-4 ${
                    star <= (product.averageRating || 0)
                      ? 'fill-yellow-400 text-yellow-400'
                      : 'text-gray-300'
                  }`}
                />
              ))}
            </div>
            <span className="text-sm font-medium">{product.averageRating?.toFixed(1)}</span>
            <span className="text-sm text-muted-foreground">({product.reviewCount} reviews)</span>
          </div>
        )}

        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-2">
            <span className="text-xl font-bold text-primary" data-testid={`text-price-${product.id}`}>
              ${product.price}
            </span>
            {hasDiscount && (
              <span className="text-sm text-muted-foreground line-through" data-testid={`text-original-price-${product.id}`}>
                ${product.originalPrice}
              </span>
            )}
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={handleViewDetails}
            className="flex-1"
          >
            <Eye className="w-4 h-4 mr-1" />
            Details
          </Button>
          <Button
            size="sm"
            className={`flex-1 ${
              isInStock
                ? 'bg-primary text-primary-foreground hover:bg-primary/90'
                : 'bg-muted text-muted-foreground cursor-not-allowed'
            }`}
            disabled={!isInStock}
            onClick={handleCheckoutClick}
            data-testid={`button-checkout-${product.id}`}
          >
            <ShoppingCart className="w-4 h-4 mr-1" />
            {isInStock ? 'Buy Now' : 'Out of Stock'}
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/product-group-card.content:
import { useState } from "react";
import { ProductGroup, Product } from "@shared/schema";
import { ShoppingCart, Package } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useLocation } from "wouter";

interface ProductGroupCardProps {
  group: ProductGroup;
}

export default function ProductGroupCard({ group }: ProductGroupCardProps) {
  const [selectedVariant, setSelectedVariant] = useState(group.variants[0]);
  const [, setLocation] = useLocation();

  const isInStock = selectedVariant.inStock && selectedVariant.stockQuantity > 0;
  const hasDiscount = selectedVariant.originalPrice && parseFloat(selectedVariant.originalPrice) > parseFloat(selectedVariant.price);

  // Create a virtual Product object for the checkout page
  const createProductFromVariant = (): Product => ({
    id: selectedVariant.id,
    name: `${group.name} - ${selectedVariant.name}`,
    description: group.description,
    price: selectedVariant.price,
    originalPrice: selectedVariant.originalPrice || null,
    category: group.category,
    game: group.game || "",
    stockQuantity: selectedVariant.stockQuantity,
    inStock: selectedVariant.inStock,
    imageUrl: group.imageUrl || null,
    deliveryUrl: selectedVariant.deliveryUrl || null,
    licenseKey: selectedVariant.licenseKey || null,
    deliveryType: group.deliveryType,
  });

  const handleCheckoutClick = () => {
    const productData = encodeURIComponent(JSON.stringify(createProductFromVariant()));
    setLocation(`/checkout/${productData}`);
  };

  return (
    <div className={`bg-card rounded-lg border border-border overflow-hidden card-hover neon-border ${!isInStock ? 'opacity-75' : ''}`} data-testid={`card-group-${group.id}`}>
      <div className="relative bg-muted/20 p-6 border-b border-border">
        <div className="flex justify-between items-start">
          <span className="px-2 py-1 bg-primary text-primary-foreground text-xs font-medium rounded-md">
            {group.category}
          </span>
          {hasDiscount && (
            <span className="px-2 py-1 bg-purple-600 text-white text-xs font-medium rounded-md">
              SALE
            </span>
          )}
        </div>
        <div className="mt-4 text-center">
          {group.imageUrl ? (
            <img 
              src={group.imageUrl} 
              alt={group.name}
              className="w-[360px] h-[360px] object-cover mx-auto mb-2 rounded-lg"
            />
          ) : (
            <Package className="w-12 h-12 text-muted-foreground mx-auto mb-2" />
          )}
          <h3 className="text-lg font-semibold" data-testid={`text-name-${group.id}`}>
            {group.name === "DMA Bundle Firmware Included" && selectedVariant.id === "dma-bundle-no-firmware" 
              ? "DMA Bundle" 
              : group.name}
          </h3>
        </div>
      </div>

      <div className="p-4">
        <div className="flex items-center gap-2 mb-2">
          <Package className="w-4 h-4 text-muted-foreground" />
          <span className="text-sm text-muted-foreground" data-testid={`text-game-${group.id}`}>
            {group.game}
          </span>
        </div>


        <p className="text-muted-foreground text-sm mb-4 line-clamp-2" data-testid={`text-description-${group.id}`}>
          {group.description}
        </p>

        {/* Variant Selector */}
        <div className="mb-4">
          <label className="text-sm font-medium mb-2 block">Duration:</label>
          <Select 
            value={selectedVariant.id} 
            onValueChange={(value) => setSelectedVariant(group.variants.find(v => v.id === value) || group.variants[0])}
            data-testid={`select-variant-${group.id}`}
          >
            <SelectTrigger className="w-full">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              {group.variants.map((variant) => (
                <SelectItem key={variant.id} value={variant.id}>
                  {variant.name} - ${variant.price}
                  {variant.originalPrice && (
                    <span className="ml-2 text-muted-foreground line-through text-xs">
                      ${variant.originalPrice}
                    </span>
                  )}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-2">
            <span className="text-2xl font-bold text-primary" data-testid={`text-price-${group.id}`}>
              ${selectedVariant.price}
            </span>
            {hasDiscount && (
              <span className="text-lg text-muted-foreground line-through" data-testid={`text-original-price-${group.id}`}>
                ${selectedVariant.originalPrice}
              </span>
            )}
          </div>

          <div className="text-right">
            <div className="text-sm text-muted-foreground">Stock</div>
            <div className={`text-sm font-medium ${isInStock ? 'text-green-500' : 'text-purple-600'}`} data-testid={`text-stock-${group.id}`}>
              {isInStock ? `${selectedVariant.stockQuantity} available` : 'Out of stock'}
            </div>
          </div>
        </div>

        <div className="space-y-2">
          <Button
            variant="outline"
            className="w-full"
            onClick={() => setLocation(`/product/${selectedVariant.id}`)}
            data-testid={`button-details-${group.id}`}
          >
            <Package className="w-4 h-4 mr-2" />
            View Details
          </Button>

          <Button
            className={`w-full px-4 py-2 rounded-lg font-medium transition-colors ${
              isInStock
                ? 'bg-primary text-primary-foreground hover:bg-primary/90'
                : 'bg-muted text-muted-foreground cursor-not-allowed'
            }`}
            disabled={!isInStock}
            onClick={handleCheckoutClick}
            data-testid={`button-checkout-${group.id}`}
          >
            <ShoppingCart className="w-4 h-4 mr-2" />
            {isInStock ? 'Checkout' : 'Out of Stock'}
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/sidebar-filters.ts.content:
import { Filter, ChevronDown } from "lucide-react";
import { Checkbox } from "@/components/ui/checkbox";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Separator } from "@/components/ui/separator";

export interface FilterState {
  categories: string[];
  games: string[];
  priceRange: string;
  showInStock: boolean;
  showOutOfStock: boolean;
}

interface DropdownFiltersProps {
  filters: FilterState;
  onFiltersChange: (filters: FilterState) => void;
}

export default function DropdownFilters({ filters, onFiltersChange }: DropdownFiltersProps) {
  const handleCategoryChange = (category: string, checked: boolean) => {
    const newCategories = checked
      ? [...filters.categories, category]
      : filters.categories.filter(c => c !== category);

    onFiltersChange({
      ...filters,
      categories: newCategories
    });
  };

  const handleGameChange = (game: string, checked: boolean) => {
    const newGames = checked
      ? [...filters.games, game]
      : filters.games.filter(g => g !== game);

    onFiltersChange({
      ...filters,
      games: newGames
    });
  };

  const handlePriceRangeChange = (value: string) => {
    onFiltersChange({
      ...filters,
      priceRange: value
    });
  };

  const handleStockChange = (type: 'inStock' | 'outOfStock', checked: boolean) => {
    onFiltersChange({
      ...filters,
      [type === 'inStock' ? 'showInStock' : 'showOutOfStock']: checked
    });
  };

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button 
          variant="outline" 
          className="min-w-32 justify-between neon-border"
          data-testid="button-filters"
        >
          <Filter className="w-4 h-4 mr-2 text-primary" />
          Filters
          <ChevronDown className="w-4 h-4 ml-2" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-80 p-6 bg-card border-border neon-border" align="start">
        <h3 className="text-lg font-semibold text-foreground mb-4" data-testid="text-filters-title">
          <Filter className="inline mr-2 text-primary w-5 h-5" />
          Filters
        </h3>

        {/* Categories */}
        <div className="mb-6">
          <h4 className="text-sm font-medium text-muted-foreground mb-3">Categories</h4>
          <div className="space-y-2">
            {["Game Cheats", "Spoofers", "Game Accounts", "Unlock Tools"].map((category) => (
              <div key={category} className="flex items-center space-x-2">
                <Checkbox
                  id={`category-${category}`}
                  checked={filters.categories.includes(category)}
                  onCheckedChange={(checked) => handleCategoryChange(category, checked as boolean)}
                  data-testid={`checkbox-category-${category.toLowerCase().replace(/\s+/g, '-')}`}
                />
                <Label htmlFor={`category-${category}`} className="text-sm">
                  {category}
                </Label>
              </div>
            ))}
          </div>
        </div>

        <Separator className="my-4" />

        {/* Games */}
        <div className="mb-6">
          <h4 className="text-sm font-medium text-muted-foreground mb-3">Games</h4>
          <div className="space-y-2">
            {["Fortnite", "CS2", "Valorant", "Rust", "R6 Siege", "Call of Duty", "Multi-Game"].map((game) => (
              <div key={game} className="flex items-center space-x-2">
                <Checkbox
                  id={`game-${game}`}
                  checked={filters.games.includes(game)}
                  onCheckedChange={(checked) => handleGameChange(game, checked as boolean)}
                  data-testid={`checkbox-game-${game.toLowerCase().replace(/\s+/g, '-')}`}
                />
                <Label htmlFor={`game-${game}`} className="text-sm">
                  {game}
                </Label>
              </div>
            ))}
          </div>
        </div>

        <Separator className="my-4" />

        {/* Price Range */}
        <div className="mb-6">
          <h4 className="text-sm font-medium text-muted-foreground mb-3">Price Range</h4>
          <RadioGroup value={filters.priceRange} onValueChange={handlePriceRangeChange}>
            <div className="flex items-center space-x-2">
              <RadioGroupItem value="all" id="price-all" data-testid="radio-price-all" />
              <Label htmlFor="price-all" className="text-sm">All Prices</Label>
            </div>
            <div className="flex items-center space-x-2">
              <RadioGroupItem value="under-10" id="price-under-10" data-testid="radio-price-under-10" />
              <Label htmlFor="price-under-10" className="text-sm">Under $10</Label>
            </div>
            <div className="flex items-center space-x-2">
              <RadioGroupItem value="10-50" id="price-10-50" data-testid="radio-price-10-50" />
              <Label htmlFor="price-10-50" className="text-sm">$10 - $50</Label>
            </div>
            <div className="flex items-center space-x-2">
              <RadioGroupItem value="50-plus" id="price-50-plus" data-testid="radio-price-50-plus" />
              <Label htmlFor="price-50-plus" className="text-sm">$50+</Label>
            </div>
          </RadioGroup>
        </div>

        <Separator className="my-4" />

        {/* Stock Status */}
        <div>
          <h4 className="text-sm font-medium text-muted-foreground mb-3">Availability</h4>
          <div className="space-y-2">
            <div className="flex items-center space-x-2">
              <Checkbox
                id="stock-in"
                checked={filters.showInStock}
                onCheckedChange={(checked) => handleStockChange('inStock', checked as boolean)}
                data-testid="checkbox-stock-in"
              />
              <Label htmlFor="stock-in" className="text-sm">In Stock</Label>
            </div>
            <div className="flex items-center space-x-2">
              <Checkbox
                id="stock-out"
                checked={filters.showOutOfStock}
                onCheckedChange={(checked) => handleStockChange('outOfStock', checked as boolean)}
                data-testid="checkbox-stock-out"
              />
              <Label htmlFor="stock-out" className="text-sm">Out of Stock</Label>
            </div>
          </div>
        </div>
      </PopoverContent>
    </Popover>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

