import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Textarea } from '@/components/ui/textarea';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '@/components/ui/alert-dialog';
import { Star, ThumbsUp, User, Verified, Edit, Trash2, Upload, X, Image as ImageIcon } from 'lucide-react';
import { format } from 'date-fns';

interface ProductReview {
  id: string;
  productId: string;
  userId: string;
  rating: number;
  title?: string;
  comment?: string;
  images?: string; // JSON string array of image URLs/base64
  isVerifiedPurchase: boolean;
  helpfulCount: number;
  createdAt: string;
  updatedAt: string;
}

interface ProductReviewsProps {
  productId: string;
  currentUserId?: string;
  onReviewAdded?: () => void;
}

interface ReviewFormData {
  rating: number;
  title: string;
  comment: string;
  images: string[]; // Array of base64 image data
}

// Star Rating Component
const StarRating = ({ 
  rating, 
  editable = false, 
  onRatingChange,
  size = 'md' 
}: { 
  rating: number;
  editable?: boolean;
  onRatingChange?: (rating: number) => void;
  size?: 'sm' | 'md' | 'lg';
}) => {
  const sizeClasses = {
    sm: 'w-4 h-4',
    md: 'w-5 h-5',
    lg: 'w-6 h-6'
  };

  return (
    <div className="flex space-x-1">
      {[1, 2, 3, 4, 5].map((star) => (
        <Star
          key={star}
          className={`${sizeClasses[size]} ${
            star <= rating 
              ? 'fill-yellow-400 text-yellow-400' 
              : 'text-gray-300'
          } ${editable ? 'cursor-pointer hover:text-yellow-400' : ''}`}
          onClick={() => editable && onRatingChange && onRatingChange(star)}
        />
      ))}
    </div>
  );
};

export default function ProductReviews({ productId, currentUserId, onReviewAdded }: ProductReviewsProps) {
  const [reviews, setReviews] = useState<ProductReview[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [hasUserReviewed, setHasUserReviewed] = useState(false);
  const [formData, setFormData] = useState<ReviewFormData>({
    rating: 5,
    title: '',
    comment: '',
    images: []
  });
  const [uploadingImages, setUploadingImages] = useState(false);
  const [editingReview, setEditingReview] = useState<ProductReview | null>(null);
  const [deletingReview, setDeletingReview] = useState<string | null>(null);

  // Fetch reviews
  const fetchReviews = async () => {
    try {
      const response = await fetch(`/api/products/${productId}/reviews`);
      if (response.ok) {
        const data = await response.json();
        setReviews(data);

        // Check if current user has reviewed
        if (currentUserId) {
          const userReview = data.find((review: ProductReview) => review.userId === currentUserId);
          setHasUserReviewed(!!userReview);
        }
      }
    } catch (error) {
      console.error('Failed to fetch reviews:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReviews();
  }, [productId, currentUserId]);

  // Submit review (create or update)
  const handleSubmitReview = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!currentUserId) return;

    setSubmitting(true);
    try {
      let response;
      if (editingReview) {
        // Update existing review
        response = await fetch(`/api/reviews/${editingReview.id}`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            ...formData,
            images: formData.images.length > 0 ? JSON.stringify(formData.images) : undefined
          }),
        });
      } else {
        // Create new review
        response = await fetch(`/api/products/${productId}/reviews`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            ...formData,
            images: formData.images.length > 0 ? JSON.stringify(formData.images) : undefined
          }),
        });
      }

      if (response.ok) {
        setShowForm(false);
        setEditingReview(null);
        setFormData({ rating: 5, title: '', comment: '', images: [] });
        fetchReviews();
        onReviewAdded?.();
      } else {
        const error = await response.json();
        alert(error.message || `Failed to ${editingReview ? 'update' : 'submit'} review`);
      }
    } catch (error) {
      console.error(`Failed to ${editingReview ? 'update' : 'submit'} review:`, error);
      alert(`Failed to ${editingReview ? 'update' : 'submit'} review`);
    } finally {
      setSubmitting(false);
    }
  };

  // Mark review as helpful
  const handleMarkHelpful = async (reviewId: string) => {
    try {
      const response = await fetch(`/api/reviews/${reviewId}/helpful`, {
        method: 'POST',
      });

      if (response.ok) {
        fetchReviews(); // Refresh to show updated helpful count
      }
    } catch (error) {
      console.error('Failed to mark review as helpful:', error);
    }
  };

  // Edit review
  const handleEditReview = (review: ProductReview) => {
    setEditingReview(review);
    setFormData({
      rating: review.rating,
      title: review.title || '',
      comment: review.comment || '',
      images: review.images ? JSON.parse(review.images) : []
    });
    setShowForm(true);
  };

  // Delete review
  const handleDeleteReview = async (reviewId: string) => {
    setDeletingReview(reviewId);
    try {
      const response = await fetch(`/api/reviews/${reviewId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        fetchReviews();
        onReviewAdded?.(); // Refresh product data
      } else {
        const error = await response.json();
        alert(error.message || 'Failed to delete review');
      }
    } catch (error) {
      console.error('Failed to delete review:', error);
      alert('Failed to delete review');
    } finally {
      setDeletingReview(null);
    }
  };

  // Cancel editing
  const handleCancelEdit = () => {
    setEditingReview(null);
    setShowForm(false);
    setFormData({ rating: 5, title: '', comment: '', images: [] });
  };

  // Image upload handling
  const handleImageUpload = async (files: FileList) => {
    if (!files || files.length === 0) return;

    // Limit to 3 images per review
    const maxImages = 3;
    const currentCount = formData.images.length;
    const remainingSlots = maxImages - currentCount;

    if (remainingSlots <= 0) {
      alert(`Maximum ${maxImages} images allowed per review`);
      return;
    }

    setUploadingImages(true);

    try {
      const newImages: string[] = [];
      const filesToProcess = Array.from(files).slice(0, remainingSlots);

      for (const file of filesToProcess) {
        // Validate file type
        if (!file.type.startsWith('image/')) {
          alert('Please select only image files');
          continue;
        }

        // Validate file size (max 5MB)
        if (file.size > 5 * 1024 * 1024) {
          alert('Image size must be less than 5MB');
          continue;
        }

        // Convert to base64
        const base64 = await new Promise<string>((resolve) => {
          const reader = new FileReader();
          reader.onload = () => resolve(reader.result as string);
          reader.readAsDataURL(file);
        });

        newImages.push(base64);
      }

      setFormData(prev => ({
        ...prev,
        images: [...prev.images, ...newImages]
      }));

    } catch (error) {
      console.error('Error uploading images:', error);
      alert('Failed to upload images');
    } finally {
      setUploadingImages(false);
    }
  };

  const removeImage = (index: number) => {
    setFormData(prev => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };

  // Parse review images for display
  const parseReviewImages = (images?: string): string[] => {
    if (!images) return [];
    try {
      return JSON.parse(images);
    } catch {
      return [];
    }
  };

  // Calculate average rating
  const averageRating = reviews.length > 0 
    ? reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length
    : 0;

  if (loading) {
    return (
      <div className="space-y-4">
        <div className="animate-pulse bg-muted h-32 rounded"></div>
        <div className="animate-pulse bg-muted h-24 rounded"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Reviews Summary */}
      <div className="border-b border-border pb-4">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-xl font-semibold">Customer Reviews</h3>
          {currentUserId && !hasUserReviewed && (
            <Button
              onClick={() => setShowForm(!showForm)}
              variant="outline"
              size="sm"
            >
              Write a Review
            </Button>
          )}
        </div>

        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <StarRating rating={Math.round(averageRating)} size="lg" />
            <span className="text-2xl font-bold">{averageRating.toFixed(1)}</span>
          </div>
          <span className="text-muted-foreground">
            ({reviews.length} review{reviews.length !== 1 ? 's' : ''})
          </span>
        </div>
      </div>

      {/* Review Form */}
      {showForm && currentUserId && (
        <Card>
          <CardHeader>
            <CardTitle>{editingReview ? 'Edit Your Review' : 'Write Your Review'}</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmitReview} className="space-y-4">
              <div>
                <Label>Rating</Label>
                <div className="mt-1">
                  <StarRating
                    rating={formData.rating}
                    editable
                    onRatingChange={(rating) => setFormData({...formData, rating})}
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="title">Review Title (Optional)</Label>
                <Input
                  id="title"
                  value={formData.title}
                  onChange={(e) => setFormData({...formData, title: e.target.value})}
                  placeholder="Summarize your experience"
                />
              </div>

              <div>
                <Label htmlFor="comment">Your Review (Optional)</Label>
                <Textarea
                  id="comment"
                  value={formData.comment}
                  onChange={(e) => setFormData({...formData, comment: e.target.value})}
                  placeholder="Tell others about your experience with this product"
                  rows={4}
                />
              </div>

              {/* Image Upload Section */}
              <div>
                <Label>Photos (Optional)</Label>
                <div className="mt-2 space-y-3">
                  {/* Image Preview Grid */}
                  {formData.images.length > 0 && (
                    <div className="grid grid-cols-3 gap-2">
                      {formData.images.map((image, index) => (
                        <div key={index} className="relative group">
                          <img
                            src={image}
                            alt={`Review image ${index + 1}`}
                            className="w-full h-20 object-cover rounded-lg border"
                          />
                          <button
                            type="button"
                            onClick={() => removeImage(index)}
                            className="absolute -top-1 -right-1 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs opacity-0 group-hover:opacity-100 transition-opacity"
                          >
                            <X className="w-3 h-3" />
                          </button>
                        </div>
                      ))}
                    </div>
                  )}

                  {/* Upload Button */}
                  {formData.images.length < 3 && (
                    <div className="border-2 border-dashed border-muted rounded-lg p-4">
                      <input
                        type="file"
                        id="image-upload"
                        multiple
                        accept="image/*"
                        onChange={(e) => e.target.files && handleImageUpload(e.target.files)}
                        className="hidden"
                        disabled={uploadingImages}
                      />
                      <label
                        htmlFor="image-upload"
                        className="flex flex-col items-center justify-center cursor-pointer text-muted-foreground hover:text-foreground transition-colors"
                      >
                        <Upload className="w-8 h-8 mb-2" />
                        <span className="text-sm font-medium">
                          {uploadingImages ? 'Uploading...' : 'Upload Photos'}
                        </span>
                        <span className="text-xs mt-1">
                          Add up to {3 - formData.images.length} more photos (max 5MB each)
                        </span>
                      </label>
                    </div>
                  )}
                </div>
              </div>

              <div className="flex space-x-2">
                <Button type="submit" disabled={submitting}>
                  {submitting ? (editingReview ? 'Updating...' : 'Submitting...') : (editingReview ? 'Update Review' : 'Submit Review')}
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  onClick={editingReview ? handleCancelEdit : () => setShowForm(false)}
                >
                  Cancel
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      )}

      {/* Reviews List */}
      <div className="space-y-4">
        {reviews.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            <Star className="w-12 h-12 mx-auto mb-2 text-muted-foreground/50" />
            <p>No reviews yet. Be the first to review this product!</p>
          </div>
        ) : (
          reviews.map((review) => (
            <Card key={review.id}>
              <CardContent className="pt-6">
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <div className="bg-muted rounded-full p-2">
                      <User className="w-4 h-4" />
                    </div>
                    <div>
                      <div className="flex items-center space-x-2">
                        <span className="font-medium">Anonymous User</span>
                        {review.isVerifiedPurchase && (
                          <Badge variant="secondary" className="text-xs">
                            <Verified className="w-3 h-3 mr-1" />
                            Verified Purchase
                          </Badge>
                        )}
                      </div>
                      <div className="flex items-center space-x-2 mt-1">
                        <StarRating rating={review.rating} size="sm" />
                        <span className="text-sm text-muted-foreground">
                          {format(new Date(review.createdAt), 'MMM dd, yyyy')}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>

                {review.title && (
                  <h4 className="font-medium mb-2">{review.title}</h4>
                )}

                {review.comment && (
                  <p className="text-muted-foreground mb-3">{review.comment}</p>
                )}

                {/* Review Images */}
                {(() => {
                  const reviewImages = parseReviewImages(review.images);
                  return reviewImages.length > 0 && (
                    <div className="mb-4">
                      <div className="grid grid-cols-3 gap-2 max-w-md">
                        {reviewImages.map((image, index) => (
                          <div key={index} className="group cursor-pointer">
                            <img
                              src={image}
                              alt={`Review image ${index + 1}`}
                              className="w-full h-20 object-cover rounded-lg border hover:shadow-md transition-shadow"
                              onClick={() => {
                                // Create modal overlay for full-size image view
                                const modal = document.createElement('div');
                                modal.className = 'fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 cursor-pointer';
                                modal.innerHTML = `
                                  <img src="${image}" alt="Review image ${index + 1}" class="max-w-full max-h-full object-contain" />
                                  <button class="absolute top-4 right-4 text-white text-2xl font-bold hover:text-gray-300">&times;</button>
                                `;
                                modal.onclick = () => document.body.removeChild(modal);
                                document.body.appendChild(modal);
                              }}
                            />
                          </div>
                        ))}
                      </div>
                      <p className="text-xs text-muted-foreground mt-2 flex items-center">
                        <ImageIcon className="w-3 h-3 mr-1" />
                        {reviewImages.length} photo{reviewImages.length !== 1 ? 's' : ''} from customer
                      </p>
                    </div>
                  );
                })()}

                <div className="flex items-center justify-between">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => handleMarkHelpful(review.id)}
                    className="text-muted-foreground hover:text-foreground"
                  >
                    <ThumbsUp className="w-4 h-4 mr-1" />
                    Helpful ({review.helpfulCount})
                  </Button>

                  {/* Review Management Buttons for Owner */}
                  {currentUserId === review.userId && (
                    <div className="flex items-center space-x-2">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleEditReview(review)}
                        className="text-muted-foreground hover:text-foreground"
                      >
                        <Edit className="w-4 h-4 mr-1" />
                        Edit
                      </Button>

                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button
                            variant="ghost"
                            size="sm"
                            className="text-muted-foreground hover:text-red-600"
                          >
                            <Trash2 className="w-4 h-4 mr-1" />
                            Delete
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Delete Review</AlertDialogTitle>
                            <AlertDialogDescription>
                              Are you sure you want to delete this review? This action cannot be undone.
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => handleDeleteReview(review.id)}
                              className="bg-red-600 hover:bg-red-700"
                              disabled={deletingReview === review.id}
                            >
                              {deletingReview === review.id ? 'Deleting...' : 'Delete'}
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/pages/product-detail.tsx.content:
import { useState, useEffect } from 'react';
import { useRoute, useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Separator } from '@/components/ui/separator';
import { ArrowLeft, Package, Check, X, ShoppingCart, Star } from 'lucide-react';
import ProductReviews from '@/components/product-reviews';
import { useAuth } from '@/hooks/use-auth';
import type { Product } from '@shared/schema';

export default function ProductDetail() {
  const [, params] = useRoute('/product/:id');
  const [, setLocation] = useLocation();
  const [product, setProduct] = useState<Product | null>(null);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  const productId = params?.id;

  useEffect(() => {
    if (!productId) return;

    const fetchProduct = async () => {
      try {
        const response = await fetch(`/api/products/${productId}`);
        if (response.ok) {
          const data = await response.json();
          setProduct(data);
        } else {
          console.error('Product not found');
        }
      } catch (error) {
        console.error('Failed to fetch product:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [productId]);

  const handleCheckout = () => {
    if (!product) return;
    const productData = encodeURIComponent(JSON.stringify(product));
    setLocation(`/checkout/${productData}`);
  };

  const handleBack = () => {
    setLocation('/products');
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-background p-4">
        <div className="max-w-6xl mx-auto">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-muted rounded w-48"></div>
            <div className="grid md:grid-cols-2 gap-8">
              <div className="h-96 bg-muted rounded"></div>
              <div className="space-y-4">
                <div className="h-8 bg-muted rounded w-3/4"></div>
                <div className="h-4 bg-muted rounded w-1/2"></div>
                <div className="h-20 bg-muted rounded"></div>
                <div className="h-12 bg-muted rounded w-32"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-background p-4">
        <div className="max-w-6xl mx-auto text-center py-20">
          <Package className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
          <h1 className="text-2xl font-bold mb-2">Product Not Found</h1>
          <p className="text-muted-foreground mb-6">The product you're looking for doesn't exist.</p>
          <Button onClick={handleBack}>
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Products
          </Button>
        </div>
      </div>
    );
  }

  const isInStock = product.inStock && product.stockQuantity > 0;
  const hasDiscount = product.originalPrice && parseFloat(product.originalPrice) > parseFloat(product.price);
  const discountPercent = hasDiscount 
    ? Math.round(((parseFloat(product.originalPrice!) - parseFloat(product.price)) / parseFloat(product.originalPrice!)) * 100)
    : 0;

  return (
    <div className="min-h-screen bg-background p-4">
      <div className="max-w-6xl mx-auto">
        {/* Back Button */}
        <Button 
          variant="ghost" 
          onClick={handleBack}
          className="mb-6 hover:bg-muted"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Products
        </Button>

        {/* Product Header */}
        <div className="grid md:grid-cols-2 gap-8 mb-8">
          {/* Product Image */}
          <div className="bg-muted/20 rounded-lg p-8 flex items-center justify-center border border-border">
            {product.imageUrl ? (
              <img 
                src={product.imageUrl} 
                alt={product.name}
                className="max-w-full h-auto rounded-lg object-cover"
                style={{ maxHeight: '400px' }}
              />
            ) : (
              <Package className="w-32 h-32 text-muted-foreground" />
            )}
          </div>

          {/* Product Info */}
          <div className="space-y-6">
            <div>
              <div className="flex items-start justify-between mb-2">
                <div className="flex flex-wrap gap-2">
                  <Badge variant="default">{product.category}</Badge>
                  <Badge variant="outline">{product.game}</Badge>
                  {hasDiscount && (
                    <Badge className="bg-purple-600 text-white">
                      -{discountPercent}% OFF
                    </Badge>
                  )}
                </div>
                <div className={`flex items-center space-x-1 px-2 py-1 rounded-full text-xs ${
                  isInStock ? 'bg-green-600 text-white' : 'bg-red-600 text-white'
                }`}>
                  {isInStock ? (
                    <>
                      <Check className="w-3 h-3" />
                      <span>{product.stockQuantity > 1 ? `${product.stockQuantity} In Stock` : 'In Stock'}</span>
                    </>
                  ) : (
                    <>
                      <X className="w-3 h-3" />
                      <span>Out of Stock</span>
                    </>
                  )}
                </div>
              </div>

              <h1 className="text-3xl font-bold mb-4">{product.name}</h1>

              {/* Rating */}
              {((product.averageRating ?? 0) > 0 && (product.reviewCount ?? 0) > 0) && (
                <div className="flex items-center space-x-2 mb-4">
                  <div className="flex items-center">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <Star
                        key={star}
                        className={`w-5 h-5 ${
                          star <= (product.averageRating || 0)
                            ? 'fill-yellow-400 text-yellow-400'
                            : 'text-gray-300'
                        }`}
                      />
                    ))}
                  </div>
                  <span className="text-lg font-medium">{product.averageRating?.toFixed(1)}</span>
                  <span className="text-muted-foreground">({product.reviewCount} reviews)</span>
                </div>
              )}

              {/* Price */}
              <div className="flex items-baseline space-x-2 mb-6">
                <span className="text-3xl font-bold text-primary">
                  ${product.price}
                </span>
                {hasDiscount && (
                  <span className="text-xl text-muted-foreground line-through">
                    ${product.originalPrice}
                  </span>
                )}
              </div>

              <p className="text-muted-foreground mb-6 leading-relaxed">
                {product.description}
              </p>
            </div>

            {/* Purchase Button */}
            <div className="space-y-3">
              <Button
                size="lg"
                className={`w-full ${
                  isInStock
                    ? 'bg-primary text-primary-foreground hover:bg-primary/90'
                    : 'bg-muted text-muted-foreground cursor-not-allowed'
                }`}
                disabled={!isInStock}
                onClick={handleCheckout}
              >
                <ShoppingCart className="w-5 h-5 mr-2" />
                {isInStock ? `Buy Now - $${product.price}` : 'Out of Stock'}
              </Button>

              {/* Delivery Info */}
              <div className="text-sm text-muted-foreground space-y-1">
                <div className="flex items-center justify-between">
                  <span>Delivery Type:</span>
                  <span className="capitalize">{product.deliveryType}</span>
                </div>
                {product.deliveryType === 'download' && (
                  <div className="flex items-center justify-between">
                    <span>Instant Download:</span>
                    <span className="text-green-600">Available after purchase</span>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>

        <Separator className="my-8" />

        {/* Product Reviews */}
        <div className="mb-8">
          <ProductReviews 
            productId={product.id} 
            currentUserId={user?.id}
            onReviewAdded={() => {
              // Refresh product data to update rating
              window.location.reload();
            }}
          />
        </div>
      </div>
    </div>
  );
}

client/src/components/advanced-search.ts.content:
import React, { useState, useEffect, useMemo } from "react";
import { useQuery } from "@tanstack/react-query";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Slider } from "@/components/ui/slider";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { Search, Filter, X, ChevronDown, SlidersHorizontal } from "lucide-react";
import { Product } from "@shared/schema";

interface AdvancedSearchProps {
  onResults: (products: Product[]) => void;
  className?: string;
}

interface SearchFilters {
  categories: string[];
  games: string[];
  priceRange: { min: number; max: number };
  inStock?: boolean;
  sortBy: 'price' | 'rating' | 'newest';
  sortOrder: 'asc' | 'desc';
}

const CATEGORIES = [
  'Cheats', 'DMA Hardware', 'Spoofer', 'External Tools', 'Private Tools'
];

const GAMES = [
  'Apex Legends', 'Fortnite', 'Rust', 'Call of Duty', 'PUBG', 'Counter-Strike', 'Valorant'
];

export default function AdvancedSearch({ onResults, className = "" }: AdvancedSearchProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [showFilters, setShowFilters] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const [filters, setFilters] = useState<SearchFilters>({
    categories: [],
    games: [],
    priceRange: { min: 0, max: 500 },
    inStock: undefined,
    sortBy: 'newest',
    sortOrder: 'desc'
  });

  // Get all products to determine actual price range
  const { data: allProducts = [] } = useQuery<Product[]>({
    queryKey: ['/api/products'],
  });

  const maxPrice = useMemo(() => {
    if (allProducts.length === 0) return 500;
    return Math.max(...allProducts.map(p => parseFloat(p.price)));
  }, [allProducts]);

  // Debounced search
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (searchQuery || filters.categories.length > 0 || filters.games.length > 0 || filters.inStock !== undefined) {
        handleSearch();
      } else {
        onResults(allProducts);
      }
    }, 300);

    return () => clearTimeout(timeoutId);
  }, [searchQuery, filters, allProducts]);

  const handleSearch = async () => {
    setIsSearching(true);
    try {
      const response = await fetch('/api/products/search/advanced', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: searchQuery,
          filters: {
            categories: filters.categories.length > 0 ? filters.categories : undefined,
            games: filters.games.length > 0 ? filters.games : undefined,
            priceRange: filters.priceRange.min > 0 || filters.priceRange.max < maxPrice ? filters.priceRange : undefined,
            inStock: filters.inStock,
            sortBy: filters.sortBy,
            sortOrder: filters.sortOrder
          }
        })
      });

      if (!response.ok) {
        throw new Error('Search failed');
      }

      const results = await response.json();
      onResults(results);
    } catch (error) {
      console.error('Search error:', error);
      onResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  const updateFilter = (key: keyof SearchFilters, value: any) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const toggleCategory = (category: string) => {
    const newCategories = filters.categories.includes(category)
      ? filters.categories.filter(c => c !== category)
      : [...filters.categories, category];
    updateFilter('categories', newCategories);
  };

  const toggleGame = (game: string) => {
    const newGames = filters.games.includes(game)
      ? filters.games.filter(g => g !== game)
      : [...filters.games, game];
    updateFilter('games', newGames);
  };

  const clearFilters = () => {
    setFilters({
      categories: [],
      games: [],
      priceRange: { min: 0, max: maxPrice },
      inStock: undefined,
      sortBy: 'newest',
      sortOrder: 'desc'
    });
  };

  const hasActiveFilters = filters.categories.length > 0 || 
                          filters.games.length > 0 || 
                          filters.priceRange.min > 0 || 
                          filters.priceRange.max < maxPrice ||
                          filters.inStock !== undefined;

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Main Search Bar */}
      <div className="relative">
        <div className="flex gap-2">
          <div className="relative flex-1">
            <Input
              type="text"
              placeholder="Search products, games, categories..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-3 bg-input text-foreground border border-border focus:border-primary search-glow transition-all duration-300"
            />
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
            {isSearching && (
              <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-primary"></div>
              </div>
            )}
          </div>
          <Button
            variant="outline"
            onClick={() => setShowFilters(!showFilters)}
            className={`px-4 ${hasActiveFilters ? 'border-primary bg-primary/10' : ''}`}
          >
            <SlidersHorizontal className="w-4 h-4 mr-2" />
            Filters
            {hasActiveFilters && (
              <Badge variant="secondary" className="ml-2 px-1 py-0 text-xs">
                {filters.categories.length + filters.games.length + (filters.inStock !== undefined ? 1 : 0)}
              </Badge>
            )}
          </Button>
        </div>
      </div>

      {/* Advanced Filters */}
      <Collapsible open={showFilters} onOpenChange={setShowFilters}>
        <CollapsibleContent>
          <Card>
            <CardHeader className="pb-4">
              <CardTitle className="flex items-center justify-between text-lg">
                <span>Advanced Filters</span>
                <Button variant="ghost" size="sm" onClick={clearFilters} disabled={!hasActiveFilters}>
                  <X className="w-4 h-4 mr-1" />
                  Clear All
                </Button>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Sorting */}
              <div className="flex gap-4">
                <div className="flex-1">
                  <label className="text-sm font-medium mb-2 block">Sort By</label>
                  <Select value={filters.sortBy} onValueChange={(value: any) => updateFilter('sortBy', value)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="newest">Newest</SelectItem>
                      <SelectItem value="price">Price</SelectItem>
                      <SelectItem value="rating">Rating</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="flex-1">
                  <label className="text-sm font-medium mb-2 block">Order</label>
                  <Select value={filters.sortOrder} onValueChange={(value: any) => updateFilter('sortOrder', value)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="desc">High to Low</SelectItem>
                      <SelectItem value="asc">Low to High</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              {/* Price Range */}
              <div>
                <label className="text-sm font-medium mb-4 block">
                  Price Range: ${filters.priceRange.min} - ${filters.priceRange.max}
                </label>
                <Slider
                  min={0}
                  max={maxPrice}
                  step={10}
                  value={[filters.priceRange.min, filters.priceRange.max]}
                  onValueChange={([min, max]) => updateFilter('priceRange', { min, max })}
                  className="w-full"
                />
              </div>

              {/* Stock Status */}
              <div>
                <label className="text-sm font-medium mb-3 block">Stock Status</label>
                <div className="flex gap-4">
                  <Button
                    variant={filters.inStock === true ? "default" : "outline"}
                    size="sm"
                    onClick={() => updateFilter('inStock', filters.inStock === true ? undefined : true)}
                  >
                    In Stock Only
                  </Button>
                  <Button
                    variant={filters.inStock === false ? "default" : "outline"}
                    size="sm"
                    onClick={() => updateFilter('inStock', filters.inStock === false ? undefined : false)}
                  >
                    Out of Stock
                  </Button>
                </div>
              </div>

              {/* Categories */}
              <div>
                <label className="text-sm font-medium mb-3 block">Categories</label>
                <div className="flex flex-wrap gap-2">
                  {CATEGORIES.map(category => (
                    <Button
                      key={category}
                      variant={filters.categories.includes(category) ? "default" : "outline"}
                      size="sm"
                      onClick={() => toggleCategory(category)}
                      className="text-xs"
                    >
                      {category}
                    </Button>
                  ))}
                </div>
              </div>

              {/* Games */}
              <div>
                <label className="text-sm font-medium mb-3 block">Games</label>
                <div className="flex flex-wrap gap-2">
                  {GAMES.map(game => (
                    <Button
                      key={game}
                      variant={filters.games.includes(game) ? "default" : "outline"}
                      size="sm"
                      onClick={() => toggleGame(game)}
                      className="text-xs"
                    >
                      {game}
                    </Button>
                  ))}
                </div>
              </div>
            </CardContent>
          </Card>
        </CollapsibleContent>
      </Collapsible>

      {/* Active Filter Tags */}
      {hasActiveFilters && (
        <div className="flex flex-wrap gap-2">
          {filters.categories.map(category => (
            <Badge key={`cat-${category}`} variant="secondary" className="px-2 py-1">
              {category}
              <button
                onClick={() => toggleCategory(category)}
                className="ml-2 hover:text-destructive"
              >
                <X className="w-3 h-3" />
              </button>
            </Badge>
          ))}
          {filters.games.map(game => (
            <Badge key={`game-${game}`} variant="secondary" className="px-2 py-1">
              {game}
              <button
                onClick={() => toggleGame(game)}
                className="ml-2 hover:text-destructive"
              >
                <X className="w-3 h-3" />
              </button>
            </Badge>
          ))}
          {filters.inStock !== undefined && (
            <Badge variant="secondary" className="px-2 py-1">
              {filters.inStock ? 'In Stock' : 'Out of Stock'}
              <button
                onClick={() => updateFilter('inStock', undefined)}
                className="ml-2 hover:text-destructive"
              >
                <X className="w-3 h-3" />
              </button>
            </Badge>
          )}
        </div>
      )}
    </div>
  );
}

client/src/components/product-recommenda.content:
import React from "react";
import { useQuery } from "@tanstack/react-query";
import { Product } from "@shared/schema";
import ProductCard from "@/components/product-card";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Star, TrendingUp, Eye, Zap } from "lucide-react";

interface ProductRecommendationsProps {
  userId?: string;
  productId?: string;
  limit?: number;
  title?: string;
  className?: string;
  variant?: 'similar' | 'personalized' | 'trending' | 'recently-viewed';
}

export default function ProductRecommendations({
  userId,
  productId,
  limit = 5,
  title,
  className = "",
  variant = 'similar'
}: ProductRecommendationsProps) {
  const { data: recommendations = [], isLoading, error } = useQuery<Product[]>({
    queryKey: ['/api/products/recommendations', userId, productId, limit],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (productId) params.append('productId', productId);
      if (limit) params.append('limit', limit.toString());

      const response = await fetch(`/api/products/recommendations?${params}`);
      if (!response.ok) {
        throw new Error('Failed to fetch recommendations');
      }
      return response.json();
    },
    enabled: true,
  });

  // Don't render if no recommendations
  if (!isLoading && recommendations.length === 0) {
    return null;
  }

  const getDefaultTitle = () => {
    switch (variant) {
      case 'similar':
        return productId ? 'Similar Products' : 'Recommended for You';
      case 'personalized':
        return 'Recommended for You';
      case 'trending':
        return 'Trending Now';
      case 'recently-viewed':
        return 'Recently Viewed';
      default:
        return 'You Might Also Like';
    }
  };

  const getIcon = () => {
    switch (variant) {
      case 'similar':
        return <Star className="w-5 h-5" />;
      case 'personalized':
        return <Zap className="w-5 h-5" />;
      case 'trending':
        return <TrendingUp className="w-5 h-5" />;
      case 'recently-viewed':
        return <Eye className="w-5 h-5" />;
      default:
        return <Star className="w-5 h-5" />;
    }
  };

  return (
    <div className={`w-full ${className}`}>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-xl">
            {getIcon()}
            {title || getDefaultTitle()}
          </CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-4">
              {Array.from({ length: limit }).map((_, i) => (
                <div key={i} className="space-y-3">
                  <Skeleton className="h-48 w-full rounded-lg" />
                  <Skeleton className="h-4 w-3/4" />
                  <Skeleton className="h-3 w-1/2" />
                  <Skeleton className="h-6 w-1/3" />
                </div>
              ))}
            </div>
          ) : error ? (
            <div className="text-center text-muted-foreground py-8">
              <p>Unable to load recommendations</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-4">
              {recommendations.map((product) => (
                <ProductCard
                  key={product.id}
                  product={product}
                  className="h-full"
                />
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

// Recently Viewed Products Component
interface RecentlyViewedProps {
  className?: string;
  limit?: number;
}

export function RecentlyViewed({ className = "", limit = 8 }: RecentlyViewedProps) {
  const { data: recentlyViewed = [], isLoading } = useQuery<Product[]>({
    queryKey: ['/api/recently-viewed'],
    queryFn: async () => {
      const response = await fetch('/api/recently-viewed', {
        credentials: 'include'
      });
      if (!response.ok) {
        // If user is not authenticated, return empty array
        if (response.status === 401) {
          return [];
        }
        throw new Error('Failed to fetch recently viewed');
      }
      return response.json();
    },
    retry: false,
    refetchOnWindowFocus: false,
  });

  // Don't render if no recently viewed products
  if (!isLoading && recentlyViewed.length === 0) {
    return null;
  }

  return (
    <div className={`w-full ${className}`}>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-xl">
            <Eye className="w-5 h-5" />
            Recently Viewed
          </CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 xl:grid-cols-8 gap-4">
              {Array.from({ length: limit }).map((_, i) => (
                <div key={i} className="space-y-2">
                  <Skeleton className="h-32 w-full rounded-lg" />
                  <Skeleton className="h-3 w-full" />
                  <Skeleton className="h-4 w-2/3" />
                </div>
              ))}
            </div>
          ) : (
            <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 xl:grid-cols-8 gap-4">
              {recentlyViewed.slice(0, limit).map((product) => (
                <div key={product.id} className="group">
                  <ProductCard
                    product={product}
                    className="h-full scale-90 group-hover:scale-95 transition-transform duration-200"
                    compact={true}
                  />
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/faq.tsx.content:
import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import Header from "@/components/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { Search, ChevronDown, MessageCircle, HelpCircle, Book, Settings, CreditCard, Shield } from "lucide-react";
import { FaqItem } from "@shared/schema";

const FAQ_CATEGORIES = [
  { id: 'general', name: 'General', icon: HelpCircle },
  { id: 'products', name: 'Products', icon: Book },
  { id: 'payment', name: 'Payment', icon: CreditCard },
  { id: 'technical', name: 'Technical', icon: Settings },
  { id: 'security', name: 'Security', icon: Shield },
  { id: 'support', name: 'Support', icon: MessageCircle }
];

export default function FAQ() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
  const [openItems, setOpenItems] = useState<Set<string>>(new Set());

  const { data: faqItems = [], isLoading } = useQuery<FaqItem[]>({
    queryKey: ['/api/faq', selectedCategory],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (selectedCategory) {
        params.append('category', selectedCategory);
      }

      const response = await fetch(`/api/faq?${params}`);
      if (!response.ok) {
        throw new Error('Failed to fetch FAQ items');
      }
      return response.json();
    },
  });

  const filteredFaqItems = faqItems.filter(item =>
    !searchQuery || 
    item.question.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.answer.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const toggleItem = (itemId: string) => {
    const newOpenItems = new Set(openItems);
    if (newOpenItems.has(itemId)) {
      newOpenItems.delete(itemId);
    } else {
      newOpenItems.add(itemId);
    }
    setOpenItems(newOpenItems);
  };

  return (
    <div className="min-h-screen bg-background text-foreground">
      <div className="falling-dots"></div>
      <Header searchQuery={searchQuery} onSearchChange={setSearchQuery} />

      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl font-bold text-foreground mb-4">
            Frequently Asked Questions
          </h1>
          <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
            Find answers to common questions about our products, services, and support. 
            Can't find what you're looking for? Contact our 24/7 support team.
          </p>
        </div>

        {/* Search Bar */}
        <div className="max-w-2xl mx-auto mb-8">
          <div className="relative">
            <Input
              type="text"
              placeholder="Search frequently asked questions..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-12 pr-4 py-3 text-lg bg-input border-border focus:border-primary"
            />
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-muted-foreground w-5 h-5" />
          </div>
        </div>

        {/* Category Filters */}
        <div className="flex flex-wrap justify-center gap-3 mb-12">
          <Button
            variant={selectedCategory === null ? "default" : "outline"}
            onClick={() => setSelectedCategory(null)}
            className="text-sm"
          >
            All Categories
          </Button>
          {FAQ_CATEGORIES.map(category => {
            const Icon = category.icon;
            return (
              <Button
                key={category.id}
                variant={selectedCategory === category.id ? "default" : "outline"}
                onClick={() => setSelectedCategory(category.id)}
                className="text-sm"
              >
                <Icon className="w-4 h-4 mr-2" />
                {category.name}
              </Button>
            );
          })}
        </div>

        {/* FAQ Items */}
        <div className="max-w-4xl mx-auto">
          {isLoading ? (
            <div className="space-y-4">
              {Array.from({ length: 6 }).map((_, i) => (
                <Card key={i}>
                  <CardContent className="p-6">
                    <div className="animate-pulse">
                      <div className="h-6 bg-muted rounded w-3/4 mb-2"></div>
                      <div className="h-4 bg-muted rounded w-full"></div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : filteredFaqItems.length === 0 ? (
            <div className="text-center py-12">
              <HelpCircle className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-2xl font-semibold text-foreground mb-2">
                {searchQuery ? 'No results found' : 'No FAQ items available'}
              </h3>
              <p className="text-muted-foreground mb-6">
                {searchQuery 
                  ? `Try adjusting your search terms or browse different categories.`
                  : 'FAQ items will appear here once they are added by our team.'
                }
              </p>
              <Button 
                variant="outline"
                onClick={() => {
                  setSearchQuery("");
                  setSelectedCategory(null);
                }}
              >
                Clear Filters
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {filteredFaqItems.map((item) => (
                <Card key={item.id} className="hover:border-primary/50 transition-colors">
                  <Collapsible
                    open={openItems.has(item.id)}
                    onOpenChange={() => toggleItem(item.id)}
                  >
                    <CollapsibleTrigger asChild>
                      <CardHeader className="cursor-pointer hover:bg-muted/50 transition-colors">
                        <div className="flex items-center justify-between">
                          <div className="flex items-start gap-3">
                            <div className="flex-shrink-0 w-8 h-8 bg-primary/10 rounded-full flex items-center justify-center mt-1">
                              <HelpCircle className="w-4 h-4 text-primary" />
                            </div>
                            <div className="text-left">
                              <CardTitle className="text-lg font-semibold text-foreground">
                                {item.question}
                              </CardTitle>
                              {item.category && (
                                <Badge variant="secondary" className="mt-2">
                                  {FAQ_CATEGORIES.find(cat => cat.id === item.category)?.name || item.category}
                                </Badge>
                              )}
                            </div>
                          </div>
                          <ChevronDown className={`w-5 h-5 text-muted-foreground transition-transform ${openItems.has(item.id) ? 'rotate-180' : ''}`} />
                        </div>
                      </CardHeader>
                    </CollapsibleTrigger>
                    <CollapsibleContent>
                      <CardContent className="pt-0 pb-6">
                        <div className="ml-11 prose prose-sm max-w-none text-muted-foreground">
                          <div dangerouslySetInnerHTML={{ __html: item.answer }} />
                        </div>
                      </CardContent>
                    </CollapsibleContent>
                  </Collapsible>
                </Card>
              ))}
            </div>
          )}
        </div>

        {/* Contact Support CTA */}
        <div className="max-w-4xl mx-auto mt-16">
          <Card className="bg-gradient-to-r from-primary/10 via-primary/5 to-primary/10 border-primary/20">
            <CardContent className="p-8 text-center">
              <MessageCircle className="w-12 h-12 text-primary mx-auto mb-4" />
              <h3 className="text-2xl font-bold text-foreground mb-2">
                Still Need Help?
              </h3>
              <p className="text-muted-foreground mb-6">
                Our expert support team is available 24/7 to help with any questions or issues.
              </p>
              <div className="flex flex-col sm:flex-row gap-4 justify-center">
                <Button size="lg" className="px-8">
                  Contact Support
                </Button>
                <Button variant="outline" size="lg" className="px-8">
                  Join Discord
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/wishlist.tsx.content:
import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Header from "@/components/header";
import ProductCard from "@/components/product-card";
import ProductRecommendations from "@/components/product-recommendations";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Heart, ShoppingCart, Trash2, Star, Filter } from "lucide-react";
import { Product } from "@shared/schema";
import { useAuth } from "@/hooks/use-auth";
import { useLocation } from "wouter";

export default function Wishlist() {
  const [searchQuery, setSearchQuery] = useState("");
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  // Get user's wishlist
  const { data: wishlistProducts = [], isLoading, error } = useQuery<Product[]>({
    queryKey: ['/api/wishlist'],
    queryFn: async () => {
      const response = await fetch('/api/wishlist');
      if (!response.ok) {
        if (response.status === 401) {
          throw new Error('Please log in to view your wishlist');
        }
        throw new Error('Failed to fetch wishlist');
      }
      return response.json();
    },
    enabled: !!user,
  });

  // Remove from wishlist mutation
  const removeFromWishlistMutation = useMutation({
    mutationFn: async (productId: string) => {
      const response = await fetch(`/api/wishlist/${productId}`, {
        method: 'DELETE',
        credentials: 'include'
      });
      if (!response.ok) {
        throw new Error('Failed to remove from wishlist');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/wishlist'] });
    },
  });

  const filteredProducts = wishlistProducts.filter(product =>
    !searchQuery || 
    product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    product.game.toLowerCase().includes(searchQuery.toLowerCase()) ||
    product.category.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const totalValue = wishlistProducts.reduce((sum, product) => sum + parseFloat(product.price), 0);
  const inStockCount = wishlistProducts.filter(product => product.inStock && product.stockQuantity > 0).length;

  if (!user) {
    return (
      <div className="min-h-screen bg-background text-foreground">
        <div className="falling-dots"></div>
        <Header searchQuery={searchQuery} onSearchChange={setSearchQuery} />

        <div className="container mx-auto px-4 py-16">
          <div className="text-center max-w-md mx-auto">
            <Heart className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
            <h1 className="text-3xl font-bold text-foreground mb-4">
              Sign In Required
            </h1>
            <p className="text-muted-foreground mb-6">
              Please sign in to view and manage your wishlist
            </p>
            <Button onClick={() => setLocation('/login')} size="lg">
              Sign In
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background text-foreground">
      <div className="falling-dots"></div>
      <Header searchQuery={searchQuery} onSearchChange={setSearchQuery} />

      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8">
          <div>
            <h1 className="text-3xl md:text-4xl font-bold text-foreground mb-2">
              My Wishlist
            </h1>
            <p className="text-muted-foreground">
              Keep track of products you're interested in
            </p>
          </div>

          {wishlistProducts.length > 0 && (
            <div className="flex flex-col sm:flex-row gap-4 mt-4 sm:mt-0">
              <Card className="px-4 py-2">
                <div className="flex items-center gap-2">
                  <Heart className="w-4 h-4 text-red-500" />
                  <span className="text-sm font-medium">{wishlistProducts.length} items</span>
                </div>
              </Card>
              <Card className="px-4 py-2">
                <div className="flex items-center gap-2">
                  <ShoppingCart className="w-4 h-4 text-green-500" />
                  <span className="text-sm font-medium">{inStockCount} in stock</span>
                </div>
              </Card>
              <Card className="px-4 py-2">
                <div className="flex items-center gap-2">
                  <Star className="w-4 h-4 text-yellow-500" />
                  <span className="text-sm font-medium">${totalValue.toFixed(2)} total</span>
                </div>
              </Card>
            </div>
          )}
        </div>

        {/* Search and Filter */}
        {wishlistProducts.length > 0 && (
          <div className="mb-8">
            <div className="flex gap-4">
              <div className="relative flex-1 max-w-md">
                <input
                  type="text"
                  placeholder="Search your wishlist..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full px-4 py-2 pl-10 bg-input border border-border rounded-lg focus:border-primary focus:outline-none"
                />
                <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
              </div>
            </div>
          </div>
        )}

        {/* Wishlist Content */}
        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {Array.from({ length: 8 }).map((_, i) => (
              <Card key={i}>
                <CardContent className="p-4">
                  <Skeleton className="h-48 w-full mb-4" />
                  <Skeleton className="h-4 w-3/4 mb-2" />
                  <Skeleton className="h-6 w-1/2" />
                </CardContent>
              </Card>
            ))}
          </div>
        ) : error ? (
          <div className="text-center py-12">
            <h2 className="text-2xl font-bold text-destructive mb-4">Error Loading Wishlist</h2>
            <p className="text-muted-foreground mb-4">{(error as Error).message}</p>
            <Button onClick={() => window.location.reload()}>Try Again</Button>
          </div>
        ) : wishlistProducts.length === 0 ? (
          <div className="text-center py-16">
            <Heart className="w-24 h-24 text-muted-foreground mx-auto mb-6" />
            <h2 className="text-3xl font-bold text-foreground mb-4">
              Your Wishlist is Empty
            </h2>
            <p className="text-lg text-muted-foreground mb-8 max-w-md mx-auto">
              Start adding products you love to keep track of them and get notified of price changes.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Button size="lg" onClick={() => setLocation('/products')}>
                Browse Products
              </Button>
              <Button variant="outline" size="lg" onClick={() => setLocation('/')}>
                Back to Home
              </Button>
            </div>
          </div>
        ) : (
          <>
            {filteredProducts.length === 0 ? (
              <div className="text-center py-12">
                <h3 className="text-2xl font-semibold text-foreground mb-2">No Results Found</h3>
                <p className="text-muted-foreground mb-4">
                  No products match your search criteria.
                </p>
                <Button variant="outline" onClick={() => setSearchQuery("")}>
                  Clear Search
                </Button>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {filteredProducts.map((product) => (
                  <div key={product.id} className="relative group">
                    <ProductCard product={product} />
                    <Button
                      variant="destructive"
                      size="sm"
                      onClick={() => removeFromWishlistMutation.mutate(product.id)}
                      disabled={removeFromWishlistMutation.isPending}
                      className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity"
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                ))}
              </div>
            )}

            <div className="text-center mt-8">
              <p className="text-muted-foreground">
                Showing {filteredProducts.length} of {wishlistProducts.length} products
              </p>
            </div>
          </>
        )}

        {/* Recommendations */}
        {wishlistProducts.length > 0 && (
          <div className="mt-16">
            <ProductRecommendations 
              variant="personalized" 
              limit={6} 
              title="You Might Also Like"
            />
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/discount-code-inpu.content:
import React, { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Loader2, Check, X, Tag, AlertCircle } from "lucide-react";

export interface DiscountCodeResult {
  code: string;
  type: 'percentage' | 'fixed';
  value: string;
  discountAmount: number;
  minOrderAmount: string;
  valid: boolean;
}

interface DiscountCodeInputProps {
  orderAmount: number;
  onDiscountApplied: (discount: DiscountCodeResult | null) => void;
  appliedDiscount?: DiscountCodeResult | null;
  className?: string;
}

export default function DiscountCodeInput({ 
  orderAmount, 
  onDiscountApplied, 
  appliedDiscount,
  className = "" 
}: DiscountCodeInputProps) {
  const [code, setCode] = useState("");
  const [error, setError] = useState<string | null>(null);

  const validateCodeMutation = useMutation({
    mutationFn: async (discountCode: string) => {
      const response = await fetch('/api/discount-codes/validate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          code: discountCode,
          orderAmount: orderAmount.toString()
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to validate discount code');
      }

      return response.json();
    },
    onSuccess: (discount: DiscountCodeResult) => {
      setError(null);
      setCode("");
      onDiscountApplied(discount);
    },
    onError: (error: Error) => {
      setError(error.message);
      onDiscountApplied(null);
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!code.trim()) return;

    setError(null);
    validateCodeMutation.mutate(code.trim());
  };

  const removeDiscount = () => {
    onDiscountApplied(null);
    setError(null);
  };

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Applied Discount Display */}
      {appliedDiscount && (
        <div className="p-4 bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-700 rounded-lg">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="p-2 bg-green-100 dark:bg-green-800 rounded-full">
                <Check className="w-4 h-4 text-green-600 dark:text-green-400" />
              </div>
              <div>
                <div className="flex items-center gap-2">
                  <span className="font-semibold text-green-800 dark:text-green-200">
                    Code Applied: {appliedDiscount.code}
                  </span>
                  <Badge variant="secondary" className="text-xs">
                    {appliedDiscount.type === 'percentage' 
                      ? `${appliedDiscount.value}% off`
                      : `$${appliedDiscount.value} off`
                    }
                  </Badge>
                </div>
                <p className="text-sm text-green-600 dark:text-green-400 mt-1">
                  You save ${appliedDiscount.discountAmount.toFixed(2)}
                </p>
              </div>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={removeDiscount}
              className="text-green-600 hover:text-green-800 dark:text-green-400 dark:hover:text-green-200"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>
        </div>
      )}

      {/* Discount Code Input Form */}
      {!appliedDiscount && (
        <div className="space-y-3">
          <div className="flex items-center gap-2 text-muted-foreground">
            <Tag className="w-4 h-4" />
            <span className="text-sm font-medium">Have a discount code?</span>
          </div>

          <form onSubmit={handleSubmit} className="space-y-3">
            <div className="flex gap-2">
              <Input
                type="text"
                placeholder="Enter discount code"
                value={code}
                onChange={(e) => setCode(e.target.value.toUpperCase())}
                className="flex-1"
                disabled={validateCodeMutation.isPending}
              />
              <Button
                type="submit"
                variant="outline"
                disabled={!code.trim() || validateCodeMutation.isPending}
                className="px-6"
              >
                {validateCodeMutation.isPending ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Checking...
                  </>
                ) : (
                  'Apply'
                )}
              </Button>
            </div>

            {error && (
              <Alert variant="destructive">
                <AlertCircle className="h-4 w-4" />
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}
          </form>
        </div>
      )}

      {/* Helper Text */}
      {!appliedDiscount && !error && (
        <p className="text-xs text-muted-foreground">
          Enter your discount code above to apply it to your order
        </p>
      )}
    </div>
  );
}

client/src/pages/admin-discount-codes.ts.content:
import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Header from "@/components/header";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  Plus, 
  Edit, 
  Trash2, 
  Calendar, 
  Tag, 
  Percent, 
  DollarSign,
  Users,
  AlertCircle,
  CheckCircle,
  Clock,
  Search
} from "lucide-react";
import { DiscountCode, InsertDiscountCode } from "@shared/schema";
import { useAuth } from "@/hooks/use-auth";
import { useLocation } from "wouter";
import { format } from "date-fns";

const DISCOUNT_TYPE_CONFIG = {
  percentage: {
    label: "Percentage",
    icon: Percent,
    color: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200"
  },
  fixed: {
    label: "Fixed Amount",
    icon: DollarSign,
    color: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
  }
};

const STATUS_CONFIG = {
  active: {
    label: "Active",
    icon: CheckCircle,
    color: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
  },
  inactive: {
    label: "Inactive",
    icon: Clock,
    color: "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200"
  },
  expired: {
    label: "Expired",
    icon: AlertCircle,
    color: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200"
  }
};

export default function AdminDiscountCodes() {
  const [searchQuery, setSearchQuery] = useState("");
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingCode, setEditingCode] = useState<DiscountCode | null>(null);
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  // Get all discount codes
  const { data: discountCodes = [], isLoading, error } = useQuery<DiscountCode[]>({
    queryKey: ['/api/admin/discount-codes'],
    queryFn: async () => {
      const response = await fetch('/api/admin/discount-codes', {
        credentials: 'include'
      });
      if (!response.ok) {
        if (response.status === 401) {
          throw new Error('Admin access required');
        }
        throw new Error('Failed to fetch discount codes');
      }
      return response.json();
    },
    enabled: !!user,
  });

  // Create discount code mutation
  const createMutation = useMutation({
    mutationFn: async (data: Omit<InsertDiscountCode, 'createdBy'>) => {
      const response = await fetch('/api/admin/discount-codes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message);
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/discount-codes'] });
      setIsCreateDialogOpen(false);
    },
  });

  // Update discount code mutation
  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<Omit<InsertDiscountCode, 'createdBy'>> }) => {
      const response = await fetch(`/api/admin/discount-codes/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message);
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/discount-codes'] });
      setEditingCode(null);
    },
  });

  // Delete discount code mutation
  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/admin/discount-codes/${id}`, {
        method: 'DELETE',
        credentials: 'include',
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message);
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/discount-codes'] });
    },
  });

  const getDiscountStatus = (code: DiscountCode) => {
    const now = new Date();
    const validFrom = new Date(code.validFrom);
    const validTo = new Date(code.validTo);

    if (!code.isActive) return 'inactive';
    if (now > validTo) return 'expired';
    if (now < validFrom) return 'inactive';
    return 'active';
  };

  const filteredCodes = discountCodes.filter(code =>
    !searchQuery || 
    code.code.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const activeCodes = discountCodes.filter(code => getDiscountStatus(code) === 'active').length;
  const totalUsage = discountCodes.reduce((sum, code) => sum + code.usedCount, 0);

  if (!user || user.role !== 'admin') {
    return (
      <div className="min-h-screen bg-background text-foreground">
        <div className="falling-dots"></div>
        <Header searchQuery={searchQuery} onSearchChange={setSearchQuery} />

        <div className="container mx-auto px-4 py-16">
          <div className="text-center max-w-md mx-auto">
            <Tag className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
            <h1 className="text-3xl font-bold text-foreground mb-4">
              Admin Access Required
            </h1>
            <p className="text-muted-foreground mb-6">
              {!user 
                ? 'Please sign in with an admin account to manage discount codes'
                : 'You need admin privileges to manage discount codes'
              }
            </p>
            <Button onClick={() => setLocation('/login')} size="lg">
              {!user ? 'Sign In' : 'Contact Admin'}
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background text-foreground">
      <div className="falling-dots"></div>
      <Header searchQuery={searchQuery} onSearchChange={setSearchQuery} />

      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8">
          <div>
            <h1 className="text-3xl md:text-4xl font-bold text-foreground mb-2">
              Discount Code Management
            </h1>
            <p className="text-muted-foreground">
              Create and manage discount codes for your customers
            </p>
          </div>

          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button size="lg" className="mt-4 sm:mt-0">
                <Plus className="w-4 h-4 mr-2" />
                Create Discount Code
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-md">
              <DialogHeader>
                <DialogTitle>Create New Discount Code</DialogTitle>
              </DialogHeader>
              <DiscountCodeForm 
                onSubmit={(data) => createMutation.mutate(data)}
                isLoading={createMutation.isPending}
                error={createMutation.error?.message}
              />
            </DialogContent>
          </Dialog>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Total Codes</p>
                  <p className="text-3xl font-bold">{discountCodes.length}</p>
                </div>
                <Tag className="w-8 h-8 text-primary" />
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Active Codes</p>
                  <p className="text-3xl font-bold text-green-600">{activeCodes}</p>
                </div>
                <CheckCircle className="w-8 h-8 text-green-600" />
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Total Usage</p>
                  <p className="text-3xl font-bold text-blue-600">{totalUsage}</p>
                </div>
                <Users className="w-8 h-8 text-blue-600" />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Search */}
        <div className="mb-6">
          <div className="relative max-w-md">
            <Input
              type="text"
              placeholder="Search discount codes..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
            />
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
          </div>
        </div>

        {/* Discount Codes List */}
        {isLoading ? (
          <div className="space-y-4">
            {Array.from({ length: 5 }).map((_, i) => (
              <Card key={i}>
                <CardContent className="p-6">
                  <div className="animate-pulse space-y-3">
                    <div className="flex justify-between">
                      <div className="h-5 bg-muted rounded w-32"></div>
                      <div className="h-6 bg-muted rounded w-16"></div>
                    </div>
                    <div className="h-4 bg-muted rounded w-full"></div>
                    <div className="h-4 bg-muted rounded w-3/4"></div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : error ? (
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>{(error as Error).message}</AlertDescription>
          </Alert>
        ) : filteredCodes.length === 0 ? (
          <div className="text-center py-16">
            <Tag className="w-24 h-24 text-muted-foreground mx-auto mb-6" />
            <h2 className="text-3xl font-bold text-foreground mb-4">
              No Discount Codes
            </h2>
            <p className="text-lg text-muted-foreground mb-8 max-w-md mx-auto">
              {searchQuery 
                ? 'No codes match your search criteria.'
                : 'Create your first discount code to start offering promotions to customers.'
              }
            </p>
            <Button onClick={() => setIsCreateDialogOpen(true)}>
              <Plus className="w-4 h-4 mr-2" />
              Create First Code
            </Button>
          </div>
        ) : (
          <div className="space-y-4">
            {filteredCodes.map((code) => {
              const status = getDiscountStatus(code);
              const statusConfig = STATUS_CONFIG[status];
              const typeConfig = DISCOUNT_TYPE_CONFIG[code.type as keyof typeof DISCOUNT_TYPE_CONFIG];
              const StatusIcon = statusConfig.icon;
              const TypeIcon = typeConfig.icon;

              return (
                <Card key={code.id} className="hover:border-primary/50 transition-colors">
                  <CardContent className="p-6">
                    <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-3">
                          <h3 className="text-xl font-bold font-mono">{code.code}</h3>
                          <Badge className={statusConfig.color}>
                            <StatusIcon className="w-3 h-3 mr-1" />
                            {statusConfig.label}
                          </Badge>
                          <Badge className={typeConfig.color}>
                            <TypeIcon className="w-3 h-3 mr-1" />
                            {code.type === 'percentage' ? `${code.value}%` : `$${code.value}`}
                          </Badge>
                        </div>

                        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                          <div>
                            <p className="text-muted-foreground">Usage</p>
                            <p className="font-medium">
                              {code.usedCount} / {code.maxUses ? code.maxUses : 'âˆž'}
                            </p>
                          </div>
                          <div>
                            <p className="text-muted-foreground">Min Order</p>
                            <p className="font-medium">${code.minOrderAmount || '0.00'}</p>
                          </div>
                          <div>
                            <p className="text-muted-foreground">Valid From</p>
                            <p className="font-medium">{format(new Date(code.validFrom), 'MMM d')}</p>
                          </div>
                          <div>
                            <p className="text-muted-foreground">Valid To</p>
                            <p className="font-medium">{format(new Date(code.validTo), 'MMM d')}</p>
                          </div>
                        </div>
                      </div>

                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => setEditingCode(code)}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          variant="destructive"
                          size="sm"
                          onClick={() => {
                            if (confirm(`Delete discount code "${code.code}"?`)) {
                              deleteMutation.mutate(code.id);
                            }
                          }}
                          disabled={deleteMutation.isPending}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        )}

        {/* Edit Dialog */}
        <Dialog open={!!editingCode} onOpenChange={() => setEditingCode(null)}>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>Edit Discount Code</DialogTitle>
            </DialogHeader>
            {editingCode && (
              <DiscountCodeForm 
                initialData={editingCode}
                onSubmit={(data) => updateMutation.mutate({ id: editingCode.id, data })}
                isLoading={updateMutation.isPending}
                error={updateMutation.error?.message}
              />
            )}
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

// Discount Code Form Component
interface DiscountCodeFormProps {
  onSubmit: (data: Omit<InsertDiscountCode, 'createdBy'>) => void;
  isLoading?: boolean;
  error?: string;
  initialData?: Partial<DiscountCode>;
}

function DiscountCodeForm({ onSubmit, isLoading, error, initialData }: DiscountCodeFormProps) {
  const [formData, setFormData] = useState({
    code: initialData?.code || '',
    type: initialData?.type || 'percentage',
    value: initialData?.value || '',
    minOrderAmount: initialData?.minOrderAmount || '',
    maxUses: initialData?.maxUses?.toString() || '',
    isActive: initialData?.isActive ?? true,
    validFrom: initialData?.validFrom ? format(new Date(initialData.validFrom), 'yyyy-MM-dd') : format(new Date(), 'yyyy-MM-dd'),
    validTo: initialData?.validTo ? format(new Date(initialData.validTo), 'yyyy-MM-dd') : format(new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), 'yyyy-MM-dd')
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit({
      code: formData.code.toUpperCase(),
      type: formData.type as 'percentage' | 'fixed',
      value: formData.value,
      minOrderAmount: formData.minOrderAmount || '0',
      maxUses: formData.maxUses ? parseInt(formData.maxUses) : undefined,
      isActive: formData.isActive,
      validFrom: new Date(formData.validFrom),
      validTo: new Date(formData.validTo)
    });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <label className="text-sm font-medium">Code</label>
        <Input
          value={formData.code}
          onChange={(e) => setFormData({ ...formData, code: e.target.value.toUpperCase() })}
          placeholder="SUMMER20"
          required
        />
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="text-sm font-medium">Type</label>
          <Select value={formData.type} onValueChange={(value) => setFormData({ ...formData, type: value })}>
            <SelectTrigger>
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="percentage">Percentage</SelectItem>
              <SelectItem value="fixed">Fixed Amount</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div>
          <label className="text-sm font-medium">Value</label>
          <Input
            type="number"
            step="0.01"
            value={formData.value}
            onChange={(e) => setFormData({ ...formData, value: e.target.value })}
            placeholder={formData.type === 'percentage' ? '20' : '10.00'}
            required
          />
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="text-sm font-medium">Min Order Amount</label>
          <Input
            type="number"
            step="0.01"
            value={formData.minOrderAmount}
            onChange={(e) => setFormData({ ...formData, minOrderAmount: e.target.value })}
            placeholder="0.00"
          />
        </div>

        <div>
          <label className="text-sm font-medium">Max Uses</label>
          <Input
            type="number"
            value={formData.maxUses}
            onChange={(e) => setFormData({ ...formData, maxUses: e.target.value })}
            placeholder="Unlimited"
          />
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="text-sm font-medium">Valid From</label>
          <Input
            type="date"
            value={formData.validFrom}
            onChange={(e) => setFormData({ ...formData, validFrom: e.target.value })}
            required
          />
        </div>

        <div>
          <label className="text-sm font-medium">Valid To</label>
          <Input
            type="date"
            value={formData.validTo}
            onChange={(e) => setFormData({ ...formData, validTo: e.target.value })}
            required
          />
        </div>
      </div>

      <div className="flex items-center space-x-2">
        <input
          type="checkbox"
          id="isActive"
          checked={formData.isActive}
          onChange={(e) => setFormData({ ...formData, isActive: e.target.checked })}
        />
        <label htmlFor="isActive" className="text-sm font-medium">Active</label>
      </div>

      {error && (
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <Button type="submit" disabled={isLoading} className="w-full">
        {isLoading ? 'Creating...' : (initialData ? 'Update Code' : 'Create Code')}
      </Button>
    </form>
  );
}

client/src/pages/purchase-history.tsx.content:
import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Header from "@/components/header";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  ShoppingBag, 
  Download, 
  Calendar, 
  Search, 
  Filter,
  CheckCircle,
  Clock,
  AlertCircle,
  RefreshCw,
  Key,
  Copy,
  Eye
} from "lucide-react";
import { Order } from "@shared/schema";
import { useAuth } from "@/hooks/use-auth";
import { useLocation } from "wouter";
import { format } from "date-fns";

const ORDER_STATUS_CONFIG = {
  pending: {
    label: "Pending",
    icon: Clock,
    color: "bg-yellow-500/10 text-yellow-500 border-yellow-500/20"
  },
  confirmed: {
    label: "Confirmed", 
    icon: CheckCircle,
    color: "bg-blue-500/10 text-blue-500 border-blue-500/20"
  },
  completed: {
    label: "Completed",
    icon: CheckCircle,
    color: "bg-green-500/10 text-green-500 border-green-500/20"
  },
  cancelled: {
    label: "Cancelled",
    icon: AlertCircle,
    color: "bg-red-500/10 text-red-500 border-red-500/20"
  }
};

export default function PurchaseHistory() {
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  // Get user's order history
  const { data: orders = [], isLoading, error } = useQuery<Order[]>({
    queryKey: ['/api/orders'],
    queryFn: async () => {
      const response = await fetch('/api/orders', {
        credentials: 'include'
      });
      if (!response.ok) {
        if (response.status === 401) {
          throw new Error('Please log in to view your purchase history');
        }
        throw new Error('Failed to fetch purchase history');
      }
      return response.json();
    },
    enabled: !!user,
  });

  // Reorder mutation - add product back to cart
  const reorderMutation = useMutation({
    mutationFn: async (productId: string) => {
      // In a real app, this would add to cart or redirect to product
      setLocation(`/product/${productId}`);
    },
  });

  // Copy license key to clipboard
  const copyLicenseKey = async (licenseKey: string) => {
    try {
      await navigator.clipboard.writeText(licenseKey);
      // You might want to show a toast notification here
    } catch (error) {
      console.error('Failed to copy license key:', error);
    }
  };

  const filteredOrders = orders.filter(order => {
    const matchesSearch = !searchQuery || 
      order.productName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      order.id.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = statusFilter === "all" || order.status === statusFilter;

    return matchesSearch && matchesStatus;
  });

  const totalOrders = orders.length;
  const completedOrders = orders.filter(order => order.status === 'completed').length;
  const totalSpent = orders
    .filter(order => order.status !== 'cancelled')
    .reduce((sum, order) => sum + parseFloat(order.productPrice), 0);

  if (!user) {
    return (
      <div className="min-h-screen bg-background text-foreground">
        <div className="falling-dots"></div>
        <Header searchQuery={searchQuery} onSearchChange={setSearchQuery} />

        <div className="container mx-auto px-4 py-16">
          <div className="text-center max-w-md mx-auto">
            <ShoppingBag className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
            <h1 className="text-3xl font-bold text-foreground mb-4">
              Sign In Required
            </h1>
            <p className="text-muted-foreground mb-6">
              Please sign in to view your purchase history
            </p>
            <Button onClick={() => setLocation('/login')} size="lg">
              Sign In
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background text-foreground">
      <div className="falling-dots"></div>
      <Header searchQuery={searchQuery} onSearchChange={setSearchQuery} />

      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8">
          <div>
            <h1 className="text-3xl md:text-4xl font-bold text-foreground mb-2">
              Purchase History
            </h1>
            <p className="text-muted-foreground">
              Track your orders, download products, and manage your purchases
            </p>
          </div>

          {orders.length > 0 && (
            <div className="flex flex-col sm:flex-row gap-4 mt-4 sm:mt-0">
              <Card className="px-4 py-2">
                <div className="flex items-center gap-2">
                  <ShoppingBag className="w-4 h-4 text-blue-500" />
                  <span className="text-sm font-medium">{totalOrders} orders</span>
                </div>
              </Card>
              <Card className="px-4 py-2">
                <div className="flex items-center gap-2">
                  <CheckCircle className="w-4 h-4 text-green-500" />
                  <span className="text-sm font-medium">{completedOrders} completed</span>
                </div>
              </Card>
              <Card className="px-4 py-2">
                <div className="flex items-center gap-2">
                  <span className="text-sm font-medium">${totalSpent.toFixed(2)} total</span>
                </div>
              </Card>
            </div>
          )}
        </div>

        {/* Search and Filters */}
        {orders.length > 0 && (
          <div className="mb-8">
            <div className="flex flex-col sm:flex-row gap-4">
              <div className="relative flex-1 max-w-md">
                <Input
                  type="text"
                  placeholder="Search orders..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                />
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
              </div>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-[180px]">
                  <Filter className="w-4 h-4 mr-2" />
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Orders</SelectItem>
                  <SelectItem value="pending">Pending</SelectItem>
                  <SelectItem value="confirmed">Confirmed</SelectItem>
                  <SelectItem value="completed">Completed</SelectItem>
                  <SelectItem value="cancelled">Cancelled</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        )}

        {/* Orders List */}
        {isLoading ? (
          <div className="space-y-4">
            {Array.from({ length: 5 }).map((_, i) => (
              <Card key={i}>
                <CardContent className="p-6">
                  <div className="animate-pulse">
                    <div className="flex justify-between items-start mb-4">
                      <div className="space-y-2">
                        <div className="h-5 bg-muted rounded w-48"></div>
                        <div className="h-4 bg-muted rounded w-32"></div>
                      </div>
                      <div className="h-6 bg-muted rounded w-20"></div>
                    </div>
                    <div className="h-4 bg-muted rounded w-24"></div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : error ? (
          <div className="text-center py-12">
            <h2 className="text-2xl font-bold text-destructive mb-4">Error Loading Orders</h2>
            <p className="text-muted-foreground mb-4">{(error as Error).message}</p>
            <Button onClick={() => window.location.reload()}>Try Again</Button>
          </div>
        ) : orders.length === 0 ? (
          <div className="text-center py-16">
            <ShoppingBag className="w-24 h-24 text-muted-foreground mx-auto mb-6" />
            <h2 className="text-3xl font-bold text-foreground mb-4">
              No Purchase History
            </h2>
            <p className="text-lg text-muted-foreground mb-8 max-w-md mx-auto">
              You haven't made any purchases yet. Start browsing our products to make your first order!
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Button size="lg" onClick={() => setLocation('/products')}>
                Browse Products
              </Button>
              <Button variant="outline" size="lg" onClick={() => setLocation('/')}>
                Back to Home
              </Button>
            </div>
          </div>
        ) : (
          <>
            {filteredOrders.length === 0 ? (
              <div className="text-center py-12">
                <h3 className="text-2xl font-semibold text-foreground mb-2">No Orders Found</h3>
                <p className="text-muted-foreground mb-4">
                  No orders match your search criteria.
                </p>
                <Button 
                  variant="outline" 
                  onClick={() => {
                    setSearchQuery("");
                    setStatusFilter("all");
                  }}
                >
                  Clear Filters
                </Button>
              </div>
            ) : (
              <div className="space-y-6">
                {filteredOrders.map((order) => {
                  const statusConfig = ORDER_STATUS_CONFIG[order.status as keyof typeof ORDER_STATUS_CONFIG];
                  const StatusIcon = statusConfig?.icon || Clock;

                  return (
                    <Card key={order.id} className="hover:border-primary/50 transition-colors">
                      <CardHeader>
                        <div className="flex flex-col sm:flex-row justify-between items-start gap-4">
                          <div className="flex-1">
                            <div className="flex items-center gap-3 mb-2">
                              <CardTitle className="text-xl font-semibold">
                                {order.productName}
                              </CardTitle>
                              <Badge className={statusConfig?.color}>
                                <StatusIcon className="w-3 h-3 mr-1" />
                                {statusConfig?.label}
                              </Badge>
                            </div>
                            <div className="space-y-1 text-sm text-muted-foreground">
                              <div className="flex items-center gap-2">
                                <Calendar className="w-4 h-4" />
                                <span>Ordered on {format(new Date(order.createdAt), 'MMM d, yyyy')}</span>
                              </div>
                              <div className="flex items-center gap-2">
                                <span>Order ID: {order.id.slice(0, 8)}</span>
                              </div>
                              <div className="flex items-center gap-2">
                                <span>Payment: {order.paymentMethod}</span>
                              </div>
                            </div>
                          </div>

                          <div className="text-right">
                            <div className="text-2xl font-bold text-primary mb-2">
                              ${parseFloat(order.productPrice).toFixed(2)}
                            </div>
                            {order.discountAmount && parseFloat(order.discountAmount) > 0 && (
                              <div className="text-sm text-green-600">
                                Saved ${parseFloat(order.discountAmount).toFixed(2)}
                              </div>
                            )}
                          </div>
                        </div>
                      </CardHeader>

                      <CardContent className="pt-0">
                        {/* License Key & Download Section */}
                        {order.status === 'completed' && order.licenseKey && (
                          <div className="bg-muted/50 rounded-lg p-4 mb-4">
                            <h4 className="font-semibold mb-3 flex items-center gap-2">
                              <Key className="w-4 h-4" />
                              Product Access
                            </h4>
                            <div className="space-y-3">
                              <div className="flex items-center gap-2 text-sm">
                                <span className="text-muted-foreground">License Key:</span>
                                <code className="bg-background px-2 py-1 rounded text-xs font-mono">
                                  {order.licenseKey}
                                </code>
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  onClick={() => copyLicenseKey(order.licenseKey!)}
                                  className="h-6 px-2"
                                >
                                  <Copy className="w-3 h-3" />
                                </Button>
                              </div>

                              {order.downloadUrl && (
                                <div className="flex gap-2">
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    onClick={() => order.downloadUrl && window.open(order.downloadUrl, '_blank')}
                                  >
                                    <Download className="w-4 h-4 mr-2" />
                                    Download ({order.downloadCount || 0} downloads)
                                  </Button>
                                </div>
                              )}
                            </div>
                          </div>
                        )}

                        {/* Action Buttons */}
                        <div className="flex flex-wrap gap-3">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => setLocation(`/orders/${order.id}`)}
                          >
                            <Eye className="w-4 h-4 mr-2" />
                            View Details
                          </Button>

                          {order.productId && (
                            <Button
                              variant="outline" 
                              size="sm"
                              onClick={() => reorderMutation.mutate(order.productId!)}
                              disabled={reorderMutation.isPending}
                            >
                              <RefreshCw className="w-4 h-4 mr-2" />
                              Buy Again
                            </Button>
                          )}

                          {order.status === 'completed' && (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => setLocation(`/review/${order.productId}`)}
                            >
                              Write Review
                            </Button>
                          )}
                        </div>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>
            )}

            <div className="text-center mt-8">
              <p className="text-muted-foreground">
                Showing {filteredOrders.length} of {orders.length} orders
              </p>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
